// Declare your genre arrays here

// Make sure to declare your HTML elements as variables! 

// Submit Button 
let userSuggestions = new Object();
let genresList = document.getElementById("genresList");
let lazySolution = false;
userSuggestions["Cool"] = ["<a href='No'> <img class='Bored' src='Tedious' alt='Stressful'> </a>"];
let submitButton = document.querySelectorAll("button"); 
let games = document.querySelector(".games");
let survivalGame = ["<img class= 'minecraft' src='data:image/jpeg;base64,' alt='Image of Minecraft update.'>", "<img class='terraria' src='https://hb.imgix.net/b7c65e77a5f126a1024a8459ec0fd08f879860f1.jpeg?auto=compress,format&fit=crop&h=353&w=616&s=993b7e1d9df1e22a531675612b644e59' alt='Image of Terraria logo'>"];
let PVPgames = ["<img class='brawlStars' src='data:image/jpeg;base64,' alt='Image of brawl stars characters'>", "<img class='roblox' src='data:image/jpeg;base64,' alt='Image of roblox logo'>", "<img class= 'valorant' src='data:image/jpeg;base64,' alt='Image of Valorant characters'>"];
let horrorGames = ["<img class='doors' src='data:image/jpeg;base64,'>", "<img class='FNAF' src='data:image/jpeg;base64,' alt='Image of doors thumbnail with a door open'>"];

userSuggestions["survival"] = survivalGame;
userSuggestions["pvp"] = PVPgames;
userSuggestions["horror"] = horrorGames;

function addGames(gameList) {
    for (let game of gameList) {
         games.insertAdjacentHTML("afterBegin", game);
    }
}

console.log(submitButton);
for (let i= 0; i < submitButton.length; i++) {
   	submitButton.item(i).addEventListener("mousedown", function(){
       
        let displayedGames = games.childNodes;
        for (let j = displayedGames.length -1; j >= 0; j--) {
            displayedGames.item(j).remove();
        }
       
        submitButton.item(i).style.backgroundImage="linear-gradient(to right, blue,darkgreen), url('data:image/png;base64,')";
        switch(submitButton.item(i).className){
            case "submitButton":
                let type = document.querySelector(".type").value.toLowerCase();
                lazySolution = false;
                for (let key in userSuggestions) {
                    if (key.toLowerCase() === type) {
                        console.log(userSuggestions[key]);
                        for (let game of userSuggestions[key]) {
                            games.insertAdjacentHTML("afterBegin", game);
                        }
                        lazySolution = true;
                        break;
                    }
                }
                if (lazySolution === true) {
                    break;
                }
                console.log("This still ran");
                games.insertAdjacentHTML("afterBegin", "<p> Sorry! Your genre isn't included in this list! You can suggest a new game with a new genre above! </p>");
                break;
            case "suggestButton":
                let URL = document.querySelector(".URL").value;
                let genre = document.querySelector(".genre").value.toLowerCase();
                let describe = document.querySelector(".describe").value;
                let gameLink = document.querySelector(".gameLink").value;
                let gameName = document.querySelector(".name").value;
                
                lazySolution = false;
                for (let genreKey in userSuggestions) {
                    if (genreKey.toLowerCase() === genre) {
                        userSuggestions[genreKey].push("<a href='" + gameLink + "'> <img class='" + gameName + "' src='" + URL + "' alt='" + describe + "'> </a>");
                        console.log(userSuggestions[genreKey]);
                        console.log(genreKey);                 

                        lazySolution = true;
                        console.log("THis has ran");
                    }
                }

                if(lazySolution) {
                    break;
                }
                
                console.log("this still ran for some reason");
                
                userSuggestions[genre.toLowerCase()] = ["<a href='" + gameLink + "'> <img class='" + gameName + "' src='" + URL + "' alt='" + describe + "'> </a>"];
                genresList.insertAdjacentHTML("beforeEnd", "<option value='" + genre + "'> </option>");
                console.log(userSuggestions[genre]);
        }

    });
    submitButton.item(i).addEventListener("mouseup", function(){
        submitButton.item(i).style.backgroundImage="linear-gradient(to right, dodgerblue, green)";
    });
}


